def get_all_movie_names(name):
    movie_dict = {}
    f = open(name, 'r')
    for line in f:
        line_elements = line.split(' +++$+++ ')
        movie_dict[line_elements[0]] = {
            'name': line_elements[1],
            'genre': line_elements[5].replace(r'[', '').replace(']', '').replace('\n', '').replace('\'', '').replace(
                ' ', '').split(',')
        }

    return movie_dict


def get_movies_based_on_genre(movie_dict, genre_list):
    final_movie_dict = {}
    for key in movie_dict:
        for genre in genre_list:
            if genre in movie_dict[key]['genre']:
                final_movie_dict[key] = movie_dict[key]
                break
    return final_movie_dict


